
public class casting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		byte b=13;
		System.out.println("byte b "+b);
		int a=b;
		System.out.println("int a "+a);
		float f=89.90f;
		System.out.println("float f "+f);
		a=(int)f;
		double d=f;
		System.out.println("double d "+d);
      //  System.out.println();
	}

}
